import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // 추가

function Quiz() {
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [answer, setAnswer] = useState('');
  const [pass, setPass] = useState(null);
  const [count, setCount] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState([]);

  const navigate = useNavigate(); // 페이지 이동용

  const fetchQuiz = async () => {
    setPass(null);
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('http://localhost:9090/api/quiz/random');
      setQuiz(response.data);
    } catch (err) {
      setError('퀴즈를 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const postAnswer = async (id, answer) => {
    try {
      const response = await axios.post('http://localhost:9090/api/quiz/compareAnswer', {
        id,
        answer,
      });

      const result = response.data;
      setPass(result);

      console.log(result);
      if (result > 0) {

        setCorrectAnswers(prev => [
          ...prev,
          {
            id: quiz.id,
          },
        ]);

        setCount(count + 1);
        setTimeout(() => {
          fetchQuiz();
          setAnswer('');
        }, 1000);
      } else {

        if(count === 0){
          alert("퀴즈를 한개도 못맞췄어요 재도전 해주세요");

        }

        // ❗ 정답이 틀린 경우 → register 페이지로 이동
        navigate('/Quizregister', {
          state: {
            quizId: id,
            userAnswer: answer,
            score: count,
            correct : correctAnswers,
          },
        });
      }
    } catch (error) {
      console.error('에러 발생:', error);
    }
  };

  useEffect(() => {
    fetchQuiz();
  }, []);

  const handleAnswer = () => {
    if (quiz) {
      postAnswer(quiz.id, answer);
    }
  };

  if (loading) return <p>로딩중...</p>;
  if (error) return <p>{error}</p>;
  if (!quiz) return <p>퀴즈가 없습니다.</p>;

  return (
    <div>
      <h3>{quiz.question}</h3>
      <h5>{quiz.hint}</h5>
      <input
        type="text"
        value={answer}
        onChange={(e) => setAnswer(e.target.value)}
        onKeyDown={(e) => {
          if (e.key === 'Enter') handleAnswer();
        }}
      />
      <button onClick={handleAnswer}>제출</button>
    </div>
  );
}

export default Quiz;
