import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

function Home() {
  const navigate = useNavigate();

  const handleKakaoLogin = () => {
    window.location.href = "http://localhost:9090/api/kakao/login";
  };

  useEffect(() => {
  // 예: URL 쿼리에서 토큰을 꺼내거나 (권장하지는 않음)
  const params = new URLSearchParams(window.location.search);
  const token = params.get('token');

  if (token) {
    // localStorage 등에 저장하거나 바로 유저 정보 요청 가능
    localStorage.setItem('kakaoAccessToken', token);
    getUserInfo(token).then(user => {
      console.log('User Info:', user);
    });
  } else {
    // 이미 저장된 토큰 있으면 그걸로 정보 요청
    const storedToken = localStorage.getItem('kakaoAccessToken');
    if (storedToken) {
      getUserInfo(storedToken).then(user => {
        console.log('User Info:', user);
      });
    }
  }
}, []);


  return (
    <div>
      {/* 상단 바 */}
      <header className="header">
        <div className="logo" onClick={() => navigate('/')}>
          Quizly
        </div>
        <nav>
          <button className="nav-button notice" onClick={() => navigate('/notice')}>
            공지사항
          </button>
          <button className="nav-button login" onClick={handleKakaoLogin} >
            로그인
          </button>
        </nav>
      </header>

      {/* 본문 */}
      <main className="main-content">
        <h1>Quizly</h1>
        <h5>Quizly는 컴퓨터 관련 CS지식에 대한 퀴즈로 이루어져 있습니다.</h5>
        <button className="main-button" onClick={() => navigate('/quiz')}>
          퀴즈 풀기
        </button>
        <button className="main-button" onClick={() => navigate('/QuizRank')}>
          랭킹 보기
        </button>
      </main>
    </div>
  );
}

export default Home;
