import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Quiz() {
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [answer, setAnswer] = useState('');
  const [pass, setPass] = useState(0);


  // 퀴즈 한 개 받아오기
  const fetchQuiz = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('http://localhost:9090/api/quiz/random'); // 실제 API 주소로 변경
      setQuiz(response.data);
    } catch (err) {
      setError('퀴즈를 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const postAnswer = async (id, answer) =>{
     try {
      const response = await axios.post('http://localhost:9090/api/quiz/compareAnswer', {
        id: id,
        answer: answer, // 보내고 싶은 데이터
      });
      console.log("정답"+response.data)
      setPass(response.data);
    } catch (error) {
      console.error('에러 발생:', error);
    }
  }

  useEffect(() => {
    fetchQuiz();
  }, []);


  const handleAnswer = (answer) => {
    postAnswer(quiz.id, answer);
    console.log(quiz.id);
    console.log(pass);
    //fetchQuiz();
  };


  if (loading) return <p>로딩중...</p>;
  if (error) return <p>{error}</p>;
  if (!quiz) return <p>퀴즈가 없습니다.</p>;

  return (
    <div>
      <h3>{quiz.question}</h3>
      <h5>{quiz.hint}</h5>
       <input
        type="text"
        id="answer"
        value={answer}                 
         onKeyDown={(e) => {
          if (e.key === 'Enter') {
            handleAnswer(answer);
          }
        }}
        onChange={(e) => setAnswer(e.target.value)} 
      />
      <button onClick={() => handleAnswer(answer)}>제출</button>
    </div>
  );
}

export default Quiz;
