import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Quiz() {
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [answer, setAnswer] = useState(null);

  // 퀴즈 한 개 받아오기
  const fetchQuiz = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('http://localhost:9090/api/quiz/random'); // 실제 API 주소로 변경
      setQuiz(response.data);
    } catch (err) {
      setError('퀴즈를 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchQuiz();
  }, []);

  const handleAnswer = (answer) => {
    console.log(answer);
    // TODO: 정답 체크 API 호출 등 로직
    // 퀴즈 풀고 나서 다음 퀴즈 불러오기
    fetchQuiz();
  };

  if (loading) return <p>로딩중...</p>;
  if (error) return <p>{error}</p>;
  if (!quiz) return <p>퀴즈가 없습니다.</p>;

  return (
    <div>
      <h3>{quiz.question}</h3>
      <h5>{quiz.hint}</h5>
       <input
        type="text"
        id="answer"
        value={answer}                   // 상태에 값 바인딩
        onChange={(e) => setAnswer(e.target.value)} // 입력값 변경 시 상태 업데이트
      />
      <button onClick={() => handleAnswer(answer)}>제출</button>
    </div>
  );
}

export default Quiz;
