import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css'; // CSS 파일 import

const KAKAO_JS_KEY = '1baa51209efff6cf2bdcb128bb302fa5'; // 본인 키로 변경하세요

function Home() {
  const navigate = useNavigate();
const [kakaoLoaded, setKakaoLoaded] = useState(false);

  useEffect(() => {
    // SDK가 없으면 스크립트 추가
    if (!window.Kakao) {
      const script = document.createElement('script');
      script.src = 'https://developers.kakao.com/sdk/js/kakao.js';
      script.onload = () => {
        if (window.Kakao && !window.Kakao.isInitialized()) {
          window.Kakao.init(KAKAO_JS_KEY);
          setKakaoLoaded(true);
          console.log('카카오 SDK 로드 및 초기화 완료');
        }
      };
      document.head.appendChild(script);
    } else {
      if (!window.Kakao.isInitialized()) {
        window.Kakao.init(KAKAO_JS_KEY);
      }
      setKakaoLoaded(true);
    }
  }, []);

  const handleKakaoLogin = () => {
    if (!kakaoLoaded || !window.Kakao) {
      alert('카카오 SDK가 아직 로드되지 않았습니다. 잠시만 기다려주세요.');
      return;
    }

    window.Kakao.Auth.login({
      scope: 'profile_nickname,account_email',
      success(authObj) {
        console.log('로그인 성공', authObj);
        alert('로그인 성공! 닉네임: ' + authObj.kakao_account?.profile?.nickname);
      },
      fail(err) {
        console.error('로그인 실패', err);
      },
    });
  };

  return (
    <div>
      {/* 상단 바 */}
      <header className="header">
        <div className="logo" onClick={() => navigate('/')}>
          Quizly
        </div>
       <nav>
          <button className="nav-button notice" onClick={() => navigate('/notice')}>
            공지사항
          </button>
          <button className="nav-button login" onClick={handleKakaoLogin}>
            로그인
          </button>
        </nav>
      </header>

      {/* 본문 */}
      <main className="main-content">
        <h1>Quizly</h1>
        <h5>Quizly는 컴퓨터 관련 CS지식에 대한 퀴즈로 이루어져 있습니다.</h5>
        <button className="main-button" onClick={() => navigate('/quiz')}>
          퀴즈 풀기
        </button>
        <button className="main-button" onClick={() => navigate('/QuizRank')}>
          랭킹 보기
        </button>
      </main>
    </div>
  );
}

export default Home;
