import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function RegisterPage() {
    const [userInforArray, setUserInforArray] = useState([]);
    const navigate = useNavigate();
    const location = useLocation();
    const { quizId, userAnswer, score, correct } = location.state || {};

    const [nickname, setNickname] = useState('');

    const handleSubmit = async () => {

        if(nickname === ""){
            alert("닉네임을 입력해주세요");
            return false;
        }

        // 닉네임, 점수, 퀴즈 ID 등을 서버에 저장하거나 처리
        console.log('등록:', { nickname, quizId, userAnswer, score, correct });

        const newArray = correct.map((item) => ({
            id: item.id,
            userNm: nickname,
        }));

        const response = await axios.post('http://localhost:9090/api/quiz/rank', {
            newArray
        });


        // 등록 후 결과 페이지나 홈으로 이동
        navigate('/result', {
            state: { nickname, score },
        });
    };

    if (!quizId) return <p>잘못된 접근입니다.</p>;

    return (
        <div>
            <h2>닉네임을 입력하고 랭킹에 등록하세요</h2>
            <h3>총 맞춘 개수 : {score}</h3>
            <input
                type="text"
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
                placeholder="닉네임 입력"
            />
            <button onClick={handleSubmit}>등록</button>
        </div>
    );
}

export default RegisterPage;
